    //
    // async componentDidMount() {
    //     const {id} = this.props;
    //     let posts = await this.postService.getPost(id);
    //     this.setState({posts});
    // }

    componentDidMount() {
        const {id} = this.props;
        fetch('https://jsonplaceholder.typicode.com/posts?userId=' + id)
            .then(value => value.json())
            .then(value => this.setState({posts:value}))

    }

    render() {
        const {posts} =this.state;
        const {id} = this.props;
        posts.forEach(value => {
            console.log(value);
        })
        return (
            <div>
                {posts.map(value => <div>{value.title} - {value.body}</div>)}
            </div>
        );
    }
}

export default Posts;
